from typing import List, Tuple, Optionalimport tkinter as tk# Импортируем tb из utils, чтобы работала совместимостьfrom core.utils import tbclass CalcModule:    """Базовый класс для всех модулей."""    key: str = "base"    title: str = "Базовый модуль"    def __init__(self, app):        self.app = app        self.frame: Optional[tb.Frame] = None    def build_ui(self, parent: tb.Frame) -> tb.Frame:        """Здесь строится интерфейс модуля."""        f = tb.Frame(parent)        tb.Label(f, text="Пустой модуль").pack()        return f    def on_show(self) -> None:        pass    def toolbar_actions(self) -> List[Tuple[str, str, callable]]:        """Кнопки: (Текст, Стиль, Функция)"""        return []    def calculate(self) -> None:        pass        def parse_float(self, s: str) -> float:        return float(s.replace(",", ".").strip())